#!/bin/bash
# =============================================================================
# C2M API Pipeline Smoke Test
# =============================================================================
# Purpose: Quick sanity check to ensure pipeline is functional
# Author: Generated by Claude
# Date: September 27, 2025
# Runtime: ~30 seconds
# =============================================================================

set -uo pipefail

# Colors
GREEN='\033[0;32m'
RED='\033[0;31m'
YELLOW='\033[1;33m'
NC='\033[0m'

echo "üî• C2M API Pipeline Smoke Test"
echo "================================"
echo ""

# Track results
PASSED=0
FAILED=0

# Simple pass/fail functions
pass() {
    echo -e "${GREEN}‚úì${NC} $1"
    ((PASSED++))
}

fail() {
    echo -e "${RED}‚úó${NC} $1"
    ((FAILED++))
}

# 1. Check core files exist
echo "1. Checking core files..."
if [ -f "Makefile" ]; then
    pass "Makefile exists"
else
    fail "Makefile missing"
fi

if [ -f ".env" ]; then
    pass ".env exists"
else
    fail ".env missing"
fi

if [ -f "data_dictionary/c2mapiv2-dd.ebnf" ]; then
    pass "EBNF data dictionary exists"
else
    fail "EBNF missing"
fi

# 2. Check critical commands
echo -e "\n2. Checking commands..."
command -v make &>/dev/null && pass "make available" || fail "make not found"
command -v jq &>/dev/null && pass "jq available" || fail "jq not found"
command -v npx &>/dev/null && pass "npx available" || fail "npx not found"

# 3. Test OpenAPI generation
echo -e "\n3. Testing OpenAPI generation..."
if make generate-openapi-spec-from-ebnf-dd &>/dev/null; then
    pass "OpenAPI generation works"
    [ -f "openapi/c2mapiv2-openapi-spec-final.yaml" ] && pass "Final spec created" || fail "Final spec missing"
else
    fail "OpenAPI generation failed"
fi

# 4. Test collection generation locally
echo -e "\n4. Testing collection generation..."
if npx openapi-to-postmanv2 -s openapi/c2mapiv2-openapi-spec-final.yaml -o /tmp/test-smoke.json -p &>/dev/null; then
    pass "Collection generation works"
    rm -f /tmp/test-smoke.json
else
    fail "Collection generation failed"
fi

# 5. Check Postman connectivity (if API key exists)
echo -e "\n5. Testing Postman connectivity..."
if [ -f ".env" ]; then
    source .env
    if [ -n "${POSTMAN_SERRAO_API_KEY:-}" ]; then
        response=$(curl -s -o /dev/null -w "%{http_code}" \
            -H "X-Api-Key: $POSTMAN_SERRAO_API_KEY" \
            "https://api.getpostman.com/me")
        [ "$response" = "200" ] && pass "Postman API connected" || fail "Postman API error (HTTP $response)"
    else
        echo -e "${YELLOW}‚ö†${NC} Postman API key not configured"
    fi
fi

# 6. Check critical Makefile targets
echo -e "\n6. Checking Makefile targets..."
grep -q "^postman-instance-build-and-test:" Makefile && pass "Main build target exists" || fail "Main build target missing"
grep -q "^postman-cleanup-all:" Makefile && pass "Cleanup target exists" || fail "Cleanup target missing"

# Summary
echo -e "\n================================"
echo "SUMMARY: $PASSED passed, $FAILED failed"

if [ $FAILED -eq 0 ]; then
    echo -e "${GREEN}‚úÖ Smoke test PASSED - Pipeline appears functional${NC}"
    exit 0
else
    echo -e "${RED}‚ùå Smoke test FAILED - Pipeline has issues${NC}"
    exit 1
fi